                    }
                } catch (err) {
                    console.error('ÈáçËØïËé∑ÂèñÁÆÄÂéÜÂéÜÂè≤Âá∫Èîô:', err);
                    setError('ÈáçËØïÂ§±Ë¥•ÔºåÊó†Ê≥ïËøûÊé•Âà∞ÊúçÂä°Âô®');
                } finally {
