                            <div className="error-message">{error}</div>
                            <button className="retry-button" onClick={retryLoading}>ÈáçËØï</button>
                        </div>
                    ) : selectedResume ? (
                        // ÁÆÄÂéÜËØ¶ÊÉÖËßÜÂõæ
