import React, { useState, useEffect } from 'react';
import './ResumeReviewHistory.css';
import axios from 'axios';

function ResumeReviewHistory({ onBack }) {
    const [loading, setLoading] = useState(true);
    const [resumeHistory, setResumeHistory] = useState([]);
    const [selectedResume, setSelectedResume] = useState(null);
    const [error, setError] = useState(null);

    // 获取简历修改历史
    useEffect(() => {
        const fetchResumeHistory = async () => {
            try {
                setLoading(true);
                console.log('开始请求历史数据...');
                const response = await axios.get('http://localhost:8080/api/resume/page', {
                    timeout: 10000, // 设置超时时间
                    headers: {
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    }
                });
                console.log('收到响应:', response);
                if (response.data && response.data.success) {
                    const records = response.data.data.records || [];
                    console.log('解析记录:', records);
                    setResumeHistory(records);
                } else {
                    console.error('API返回错误:', response.data);
                    setError(`获取历史记录失败: ${response.data?.message || '未知错误'}`);
                }
            } catch (err) {
                console.error('获取简历历史出错详情:', err.message, err.response || '无响应');
                if (err.code === 'ECONNABORTED') {
                    setError('请求超时，请检查后端服务是否启动');
                } else {
                    setError('无法连接到服务器，请稍后再试');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchResumeHistory();
    }, []);

    // 查看详情
    const viewResumeDetail = async (id) => {
        try {
            setLoading(true);
            console.log(`开始请求ID为${id}的简历详情...`);
            const response = await axios.get(`http://localhost:8080/api/resume/${id}`, {
                timeout: 10000,
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });
            console.log('收到详情响应:', response);
            if (response.data && response.data.success) {
                setSelectedResume(response.data.data);
                console.log('设置选中的简历:', response.data.data);
            } else {
                console.error('API返回详情错误:', response.data);
                setError(`获取简历详情失败: ${response.data?.message || '未知错误'}`);
            }
        } catch (err) {
            console.error('获取简历详情出错:', err.message, err.response || '无响应');
            setError('无法获取简历详情，请稍后再试');
        } finally {
            setLoading(false);
        }
    };

    // 返回列表
    const backToList = () => {
        setSelectedResume(null);
        setError(null);
    };

    // 格式化日期
    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}-${date.getDate().toString().padStart(2, '0')} ${date.getHours().toString().padStart(2, '0')}:${date.getMinutes().toString().padStart(2, '0')}`;
    };

    // 重试加载数据
    const retryLoading = () => {
        setError(null);
        setLoading(true);
        // 使用setTimeout确保状态更新后再重试
        setTimeout(() => {
            const fetchResumeHistory = async () => {
                try {
                    console.log('重新尝试请求数据...');
                    const response = await axios.get('http://localhost:8080/api/resume/page', {
                        timeout: 10000,
                        headers: {
                            'Content-Type': 'application/json',
                            'Accept': 'application/json'
                        }
                    });
                    if (response.data && response.data.success) {
                        const records = response.data.data.records || [];
                        setResumeHistory(records);
                    } else {
                        setError(`获取历史记录失败: ${response.data?.message || '未知错误'}`);
                    }
                } catch (err) {
                    console.error('重试获取简历历史出错:', err);
                    setError('重试失败，无法连接到服务器');
                } finally {
                    setLoading(false);
                }
            };
            fetchResumeHistory();
        }, 100);
    };

    return (
        <div className="resume-history">
            <div className="dialog-container">
                <div className="dialog-box">
                    <h2>简历修改历史</h2>

                    {loading ? (
                        <div className="loading">加载中...</div>
                    ) : error ? (
                        <div className="error-container">
                            <div className="error-message">{error}</div>
                            <button className="retry-button" onClick={retryLoading}>重试</button>
                        </div>
                    ) : selectedResume ? (
                        // 简历详情视图
                        <div className="resume-detail">
                            <h3>修改详情</h3>
                            <div className="detail-section">
                                <h4>修改时间</h4>
                                <p>{formatDate(selectedResume.updatedTime)}</p>
                            </div>
                            <div className="detail-section">
                                <h4>修改描述</h4>
                                <p>{selectedResume.modificationDescription || '无描述'}</p>
                            </div>
                            <div className="detail-section">
                                <h4>原始内容</h4>
                                <div className="content-box">
                                    <pre>{selectedResume.originalContent}</pre>
                                </div>
                            </div>
                            <div className="detail-section">
                                <h4>修改后内容</h4>
                                <div className="content-box">
                                    <pre>{selectedResume.modifiedContent}</pre>
                                </div>
                            </div>
                            <div className="action-buttons">
                                <button className="back-button" onClick={backToList}>返回列表</button>
                            </div>
                        </div>
                    ) : resumeHistory.length > 0 ? (
                        // 简历列表视图
                        <div className="resume-list">
                            <p className="dialog-description">您的简历修改记录</p>
                            <div className="history-list">
                                {resumeHistory.map(resume => (
                                    <div key={resume.id} className="history-item" onClick={() => viewResumeDetail(resume.id)}>
                                        <div className="item-time">{formatDate(resume.createdTime)}</div>
                                        <div className="item-description">
                                            {resume.modificationDescription 
                                                ? (resume.modificationDescription.length > 50 
                                                    ? resume.modificationDescription.substring(0, 50) + '...' 
                                                    : resume.modificationDescription)
                                                : '无描述'}
                                        </div>
                                        <div className="item-arrow">➡️</div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ) : (
                        // 空状态
                        <div>
                            <p className="dialog-description">您还没有上传过简历</p>
                            <div className="empty-state">
                                <div className="empty-icon">📄</div>
                                <p>上传简历后可以在这里查看历史记录</p>
                            </div>
                        </div>
                    )}

                    <div className="action-buttons">
                        {!selectedResume && <button className="back-button" onClick={onBack}>返回上传</button>}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ResumeReviewHistory;
