import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ResumeUpload.css';

// 创建支持大数据的axios实例
const api = axios.create({
    baseURL: 'http://localhost:8080/api',
    timeout: 30000, // 增加超时时间到30秒
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    },
    maxContentLength: Infinity, // 允许无限大小的内容
    maxBodyLength: Infinity // 允许无限大小的请求体
});

function ResumeUpload({ languages, apiUrl, onViewHistory }) {
    const [resumeText, setResumeText] = useState('');
    const [modificationRequirements, setModificationRequirements] = useState('');
    const [modifiedResume, setModifiedResume] = useState('');
    const [isModifying, setIsModifying] = useState(false);
    const [sourceLanguage, setSourceLanguage] = useState('中文');
    const [targetLanguage, setTargetLanguage] = useState('中文');
    const [success, setSuccess] = useState('');
    const [isSaving, setIsSaving] = useState(false);

    const handleTextChange = (e) => {
        setResumeText(e.target.value);
    };

    const handleRequirementsChange = (e) => {
        setModificationRequirements(e.target.value);
    };

    // 格式化简历内容，使其更美观
    const formatResumeContent = (content) => {
        if (!content) return null;

        // 检查内容是否已经包含HTML标签
        if (content.includes('<') && content.includes('>')) {
            return <div dangerouslySetInnerHTML={{ __html: content }} style={{ textAlign: 'left' }} />;
        }

        const lines = content.split('\n');
        const formattedLines = [];
        let currentSectionTitle = '';
        let inProjectSection = false;

        for (let i = 0; i < lines.length; i++) {
            const line = lines[i];
            const trimmedLine = line.trim();

            // 检查是否为空行
            if (trimmedLine === '') {
                formattedLines.push(<div key={`empty-${i}`} className="section-divider"></div>);
                continue;
            }

            // 检查是否是章节标题
            const isSectionTitle = 
                /^(基本信息|教育背景|工作经验|项目经验|技能|主修课程)/.test(trimmedLine) ||
                /^(Personal Information|Education|Work Experience|Project Experience|Skills|Courses)/.test(trimmedLine);

            if (isSectionTitle) {
                currentSectionTitle = trimmedLine;
                inProjectSection = currentSectionTitle.includes('项目经验') || currentSectionTitle.includes('Project Experience');
                formattedLines.push(
                    <h3 key={`section-${i}`} className="resume-section-title">
                        {trimmedLine}
                    </h3>
                );
                continue;
            }

            // 项目标题处理
            if (inProjectSection && trimmedLine && !trimmedLine.startsWith('•') && !trimmedLine.startsWith('-') &&
                !trimmedLine.includes(':') && !trimmedLine.includes('：')) {
                formattedLines.push(
                    <h4 key={`project-${i}`} className="resume-project-title">
                        {trimmedLine}
                    </h4>
                );
                continue;
            }

            // 处理列表项 (bullets)
            if (trimmedLine.startsWith('•') || trimmedLine.startsWith('-') || trimmedLine.startsWith('*')) {
                const bulletContent = trimmedLine.substring(1).trim();
                formattedLines.push(
                    <div key={`bullet-${i}`} className="resume-bullet-point">
                        <span className="bullet-symbol">•</span>
                        <span className="bullet-content">{bulletContent}</span>
                    </div>
                );
                continue;
            }

            // 处理键值对 (如 "姓名: xxx")
            const keyValueMatch = trimmedLine.match(/^(.+?)[:：]\s*(.+)$/);
            if (keyValueMatch) {
                formattedLines.push(
                    <div key={`kv-${i}`} className="resume-key-value">
                        <span className="resume-key">{keyValueMatch[1]}：</span>
                        <span className="resume-value">{keyValueMatch[2]}</span>
                    </div>
                );
                continue;
            }

            // 处理普通段落
            formattedLines.push(
                <p key={`p-${i}`} className="resume-paragraph">
                    {trimmedLine}
                </p>
            );
        }

        return <div style={{ textAlign: 'left' }}>{formattedLines}</div>;
    };

    async function handleTextOutput() {
        setIsModifying(true);
        setModifiedResume(""); // 清空之前的结果
        setSuccess(""); // 清空成功消息

        try {
            const response = await fetch(`${apiUrl}/api/modify_resume`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    resume_text: resumeText,
                    requirements: modificationRequirements,
                    source_language: sourceLanguage,
                    target_language: targetLanguage
                })
            });

            if (!response.ok) {
                throw new Error("服务器响应错误");
            }

            const reader = response.body.getReader();
            const decoder = new TextDecoder();

            while (true) {
                const { done, value } = await reader.read();
                if (done) break;

                const text = decoder.decode(value);
                const events = text.split('\n\n');

                for (const event of events) {
                    if (!event.trim()) continue;

                    // 提取"data:"后面的JSON
                    const match = event.match(/^data:\s*(.+)$/m);
                    if (!match) continue;

                    try {
                        const data = JSON.parse(match[1]);

                        if (data.type === 'update') {
                            setModifiedResume(data.text);
                        } else if (data.type === 'error') {
                            setModifiedResume("抱歉，修改过程中出现错误，请稍后再试。");
                        } else if (data.type === 'end') {
                            setSuccess("修改成功！");
                            // 修改成功后自动保存到数据库
                            saveResultToDatabase(resumeText, data.text, modificationRequirements);
                        }
                    } catch (parseError) {
                        console.error("解析JSON失败:", parseError, match[1]);
                    }
                }
            }
        } catch (error) {
            console.error("请求失败:", error);
            setModifiedResume("抱歉，修改过程中出现错误，请稍后再试。");
        } finally {
            setIsModifying(false);
        }
    }

    // 保存结果到数据库
    const saveResultToDatabase = async (originalContent, modifiedContent, description) => {
        try {
            setIsSaving(true);
            console.log('开始保存简历修改结果到数据库...');
            console.log('原始内容长度:', originalContent.length);
            console.log('修改后内容长度:', modifiedContent.length);

            const response = await api.post('/resume', {
                originalContent: originalContent,
                modifiedContent: modifiedContent,
                modificationDescription: description || '',
                userId: 'user123', // 可以从用户登录信息中获取
                status: 1 // 1表示已完成
            });

            console.log('保存响应:', response);

            if (response.data && response.data.success) {
                setSuccess(prev => prev + " 已保存到历史记录！");
                console.log('保存成功，ID:', response.data.data.id);
            } else {
                console.error('保存失败:', response.data);
            }
        } catch (error) {
            console.error('保存到数据库出错:', error);
        } finally {
            setIsSaving(false);
        }
    };

    return (
        <div className="resume-modification-tool">
            <div className="modification-container">
                {/* 头部区域 */}
                <div className="modification-header">
                    <h2>简历修改工具</h2>
                    <button 
                        className="history-button" 
                        onClick={onViewHistory}
                    >
                        查看历史
                    </button>
                </div>

                {/* 修改要求输入框 */}
                <div className="requirements-card">
                    <h3 className="card-title">修改要求</h3>
                    <textarea 
                        id="modification-requirements"
                        className="requirements-input"
                        placeholder="请输入您对简历修改的具体要求..."
                        value={modificationRequirements}
                        onChange={handleRequirementsChange}
                    ></textarea>
                </div>

                {/* 主体内容区域 */}
                <div className="resume-comparison-container">
                    {/* 左侧原始简历 */}
                    <div className="resume-column">
                        <div className="resume-header">
                            <h3>原始简历</h3>
                            <div className="language-selector">
                                <select 
                                    value={sourceLanguage} 
                                    onChange={(e) => setSourceLanguage(e.target.value)}
                                    className="language-select"
                                >
                                    <option value="中文">中文</option>
                                    {Object.entries(languages)
                                        .filter(([k]) => k !== '中文')
                                        .map(([k, v]) => (
                                            <option key={`source-${k}`} value={k}>{v}</option>
                                        ))
                                    }
                                </select>
                            </div>
                        </div>
                        <div className="resume-content-card original-resume">
                            <textarea 
                                className="resume-text-input"
                                placeholder="请在此粘贴您的简历文本..."
                                value={resumeText}
                                onChange={handleTextChange}
                            ></textarea>
                        </div>
                    </div>

                    {/* 中间转换区域 */}
                    <div className="conversion-column">
                        <div className="conversion-arrow">→</div>
                        <button 
                            className="modify-button"
                            onClick={handleTextOutput}
                            disabled={isModifying || !resumeText.trim()}
                        >
                            {isModifying ? '修改中...' : '开始修改'}
                        </button>
                        <div className="target-language">
                            <select 
                                value={targetLanguage} 
                                onChange={(e) => setTargetLanguage(e.target.value)}
                                className="language-select"
                            >
                                <option value="中文">中文</option>
                                {Object.entries(languages)
                                    .filter(([k]) => k !== '中文')
                                    .map(([k, v]) => (
                                        <option key={`target-${k}`} value={k}>{v}</option>
                                    ))
                                }
                            </select>
                        </div>
                    </div>

                    {/* 右侧修改后简历 */}
                    <div className="resume-column">
                        <div className="resume-header">
                            <h3>修改后的简历</h3>
                            {/* 成功提示消息 */}
                            {success && (
                                <div className="success-message">
                                    <span className="success-icon">✓</span> {success}
                                    {isSaving && <span className="saving-indicator"> (保存中...)</span>}
                                </div>
                            )}
                        </div>
                        <div className="resume-content-card modified-resume">
                            {modifiedResume ? (
                                <div className="formatted-resume">
                                    {formatResumeContent(modifiedResume)}
                                </div>
                            ) : (
                                <div className="empty-content">
                                    修改后的简历将显示在这里
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ResumeUpload;
