import React, { useState } from 'react';
import axios from 'axios';
import './ResumeUpload.css';

function ResumeUpload({ languages, apiUrl, onViewHistory }) {
    const [inputMethod, setInputMethod] = useState('file'); // 'file' 或 'text'
    const [resumeText, setResumeText] = useState('');

    const handleTextChange = (e) => {
        setResumeText(e.target.value);
    };

    const switchToFileUpload = () => {
        setInputMethod('file');
    };

    const switchToTextInput = () => {
        setInputMethod('text');
    };

    return (
        <div className="resume-upload">
            <div className="dialog-container">
                <div className="dialog-box">
                    <h2>上传您的简历</h2>
                    <p className="dialog-description">请上传您的简历文件，我们将为您提供修改建议</p>

                    <div className="input-method-selector">
                        <button 
                            className={`method-button ${inputMethod === 'file' ? 'active' : ''}`}
                            onClick={switchToFileUpload}
                        >
                            上传文件
                        </button>
                        <button 
                            className={`method-button ${inputMethod === 'text' ? 'active' : ''}`}
                            onClick={switchToTextInput}
                        >
                            粘贴文本
                        </button>
                    </div>

                    {inputMethod === 'file' ? (
                        <div className="upload-area">
                            <input type="file" id="resume-file" className="file-input" />
                            <label htmlFor="resume-file" className="file-label">
                                选择文件
                            </label>
                            <p className="file-hint">支持 .pdf, .doc, .docx 格式</p>
                        </div>
                    ) : (
                        <div className="text-input-area">
                            <textarea 
                                className="resume-text-input"
                                placeholder="请在此粘贴您的简历文本..."
                                value={resumeText}
                                onChange={handleTextChange}
                            ></textarea>
                        </div>
                    )}

                    <div className="language-selector">
                        <p>选择简历语言：</p>
                        <div className="language-options">
                            {languages.map((lang, index) => (
                                <label key={index} className="language-option">
                                    <input type="radio" name="language" value={lang} 
                                        defaultChecked={index === 0} />
                                    <span>{lang}</span>
                                </label>
                            ))}
                        </div>
                    </div>

                    <div className="action-buttons">
                        <button className="submit-button">开始修改</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ResumeUpload;
